import java.util.*;

public class BaekJ_2739 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 0;
		
		Scanner in = new Scanner(System.in);
		
		a = in.nextInt();
		
		for(int i=1; i <= 9; i++) {
			
			System.out.println(a+ " * " + i + " = " + (a*i) );
		}
		
	}

}
